;(function(){

// Better wirte your code in original Javascript with the least dependency
var BaseEnv = {
	getHost: function(ori) {
		var dest = '';
	    if (location.host.indexOf('dianping.com') >= 0) {
	    	ori = ori.replace('51ping.com','dianping.com');
			if (location.host.indexOf('ppe.') >= 0) {
				dest = ori.replace('//','//ppe.');
			}else{
				dest = ori.replace('//ppe.','//');
			}
	    } else {
	    	dest = ori.replace('dianping.com','51ping.com');
	    }
	    return dest;
	}
}
window.BaseEnv = BaseEnv;
})();