/**
 *  Test ZK init
 * */
var assert = require("assert");
var zk = require("../lib/zk");

describe("zk", function () {
    describe('#init', function () {
        it("should return a Promise", function () {
            assert(zk.init() instanceof Promise);
        });

        it("should return the same promise when call the init multiple times", function () {
            var one = zk.init();
            var two = zk.init();
            assert.equal(one, two);
        });
    });
});