#!/usr/bin/env node
var core = require('../index.js');
var cli = require('cli');
cli.parse(null, ['register', 'version', 'schema']);
cli.main(function(args, options) {
  var command = cli.command;

  switch(command){

    case "version":
      core.version.apply(core, args);
      break;
    case "register":
      core.register();
      break;
    case "schema":
      core.schema();
      break;
    case "slot":
      core.slot();
      break;
    default:
      throw new Error('no command');
      break;

  }
})
